function compare_filescd('c:\ExpData');filterspec = '*.m';%if strcmp(computer,'MAC2'), filterspec = '*'; end[data_file dat_dir] = uigetfile(filterspec,'Calibration Files');command_line = sprintf('%s%s%c','cd ''',dat_dir,'''');eval(command_line);command_line = sprintf('%s%s%c','[x]=',strrep(lower(data_file),'.m',''),';');eval(command_line);[data_file dat_dir] = uigetfile(filterspec,'Calibration Files');command_line = sprintf('%s%s%c','cd ''',dat_dir,'''');eval(command_line);command_line = sprintf('%s%s%c','[y]=',strrep(lower(data_file),'.m',''),';');eval(command_line);figure('NumberTitle','off','Name','Calibration Interface','Units','normalized','position',[0 0 1 .95]);hax = axes('position',[.1 .4 .8 .5]);semilogx(x.CalibData(:,1),x.CalibData(:,2),'r');hold on;semilogx(y.CalibData(:,1),y.CalibData(:,2),'b');low_lim = floor(min(x.CalibData(:,2))/20)*20;up_lim  = ceil( max(x.CalibData(:,2))/20)*20;set(hax,'YLim',[low_lim up_lim]);drawnow;